def icon(name: str, size: str = "", color: str = "") -> str:
    """
    Get an SVG icon wrapped in a span with appropriate classes.
    
    Args:
        name: Icon name from ICONS dict
        size: Size class - "", "lg", "xl", "2xl"
        color: Optional color style
        
    Returns:
        HTML string with icon
    """
    svg = ICONS.get(name, ICONS["help-circle"])
    size_class = f" sr-icon-{size}" if size else ""
    style = f' style="color: {color};"' if color else ""
    return f'<span class="sr-icon{size_class}"{style}>{svg}</span>'


def icon_with_text(name: str, text: str, size: str = "", gap: str = "0.5rem") -> str:
    """
    Get an icon with text, properly aligned.
    
    Args:
        name: Icon name
        text: Text to display
        size: Icon size
        gap: Gap between icon and text
        
    Returns:
        HTML string with icon and text
    """
    return f'<span style="display: inline-flex; align-items: center; gap: {gap};">{icon(name, size)} {text}</span>'
